(65, 1, 'nl-NL', '', 'http://www.ccoverheid.nl/schema/gwsw', 'S', 'GWSW', 'GWSW', '', 'GWSW', 'Gegevenswoorboek Stedelijk Water, een ontologie, een speciale datastructuur die systemen en processen op het gebied van stedelijk waterbeheer beschrijft', '', '', '', '', 'Het Gegevenswoordenboek Stedelijk Water (GWSW) is een ontologie, een speciale datastructuur die systemen en processen op het gebied van stedelijk waterbeheer beschrijft. Het GWSW is een verplichte open standaard volgens de pas-toe-of-leg-uit-lijst van Forum Standaardisatie. De GWSW datastandaard, die door Stichting RIONED namens de sector wordt ontwikkeld en beheerd, werkt volgens het linked data principe. Het is onderdeel van het Semantisch Web en is gemodelleerd in RDF/RDFS/OWL-2.', '', '', '', '', '', '', NULL, NULL, 'https://data.gwsw.nl/', 'https://data.gwsw.nl/', '', '', 0, '', '', 0, '30', 'R', '2022-06-08 00:00:00', '2022-11-05 09:29:58', NULL, NULL, NULL, ''),
(66, 65, 'nl-NL', '', 'http://www.ccoverheid.nl/schema/gwsw/RDF', 'T', NULL, 'RDF', '', 'RDF', 'Basisdefinitie van modellen op basis van subject-predicate-object, in teksten ook gebruikt voor de combinatie van RDF/RDFS/OWL2', NULL, NULL, NULL, NULL, 'RDF staat voor Resource Description Framework, de basisdefinitie van modellen op basis van subject-predicate-object. In de tekst verstaan we onder RDF de combinatie van RDF met RDFS (RDF Schema) en OWL 2 (Web Ontology Language).', '', NULL, NULL, '', '', NULL, NULL, NULL, '', '', NULL, '', 0, NULL, '', NULL, '50', 'R', '2022-06-08 00:00:00', '2022-06-08 00:00:00', NULL, NULL, NULL, NULL),
(67, 65, 'nl-NL', '', 'http://www.ccoverheid.nl/schema/gwsw/ontologie', 'T', NULL, 'ontologie', '', 'Ontologie', 'Datastructuur die alle relevante entiteiten en hun onderlinge relaties en regels binnen een domein bevat. ', NULL, NULL, NULL, NULL, '', '', NULL, NULL, '', '', NULL, NULL, NULL, 'Stichting Rioned', 'https://stichtingrioned.github.io/GWSW_Ontologie_RDF/', NULL, '', 0, NULL, '', NULL, '50', 'R', '2022-06-08 00:00:00', NULL, NULL, NULL, NULL, NULL),
(68, 65, 'nl-NL', '', 'http://www.ccoverheid.nl/schema/gwsw/individu ', 'T', NULL, 'individu ', '', 'Individu ', 'Een instantie van een concept, iets uit de werkelijkheid', NULL, NULL, NULL, NULL, '', '', NULL, NULL, '', 'Individu “0980” de bestaande betonnen constructie van het soort/klasse/concept “rioolput”.', NULL, NULL, NULL, 'Stichting Rioned', 'https://stichtingrioned.github.io/GWSW_Ontologie_RDF/', NULL, '', 0, NULL, '', NULL, '50', 'R', '2022-06-08 00:00:00', '2022-06-08 00:00:00', NULL, NULL, NULL, NULL),
(69, 65, 'nl-NL', '', 'http://www.ccoverheid.nl/schema/gwsw/property ', 'T', NULL, 'property ', '', 'Property ', 'Relatie tussen subject en object ', NULL, NULL, NULL, NULL, '', '', NULL, NULL, 'Voor de relatie (tussen subject en object) zijn meerdere namen gebruikelijk (“predicate”, “property name”), GWSW hanteert in documentatie “property”. Properties zijn (confrom de NTA8035) onderverdeeld in attributen (die weer onderverdeeld zijn in aspecten en annotaties) en relaties.', '', NULL, NULL, NULL, 'Stichting Rioned', 'https://stichtingrioned.github.io/GWSW_Ontologie_RDF/', NULL, '', 0, NULL, '', NULL, '50', 'R', '2022-06-08 00:00:00', '2022-06-08 00:00:00', NULL, NULL, NULL, NULL),
