(71, 65, 'nl-NL', '', 'http://www.ccoverheid.nl/schema/gwsw/dataset', 'T', NULL, 'dataset', '', 'Dataset', 'De gegevens van individuele stedelijk water systemen, de individuals op basis van het GWSW.', NULL, NULL, NULL, NULL, '', '', NULL, NULL, 'Een dataset werd voorheen BIM - “bouwwerk informatie model” - genoemd.', '', NULL, NULL, NULL, 'Stichting Rioned', 'https://apps.gwsw.nl/doc/GWSW.orox%20Opbouw%20dataset.pdf', NULL, '', 0, NULL, '', NULL, '50', 'R', '2022-06-08 00:00:00', NULL, NULL, NULL, NULL, NULL),
(72, 65, 'nl-NL', '', 'http://www.ccoverheid.nl/schema/gwsw/triplestore', 'T', NULL, 'triplestore', '', 'Triplestore', 'Triple-gebaseerde database op RDF-basis voorzien van interfaces om de data te kunnen benaderen.', NULL, NULL, NULL, NULL, '', '', NULL, NULL, '<p>Een graph-database (refererend aan de “graaf theorie”) is een andere veel gebruikte aanduiding voor zo’n database.</p>', '', NULL, NULL, NULL, 'Stichting Rioned', 'https://apps.gwsw.nl/doc/GWSW.orox%20Opbouw%20dataset.pdf', NULL, '', 0, NULL, '', NULL, '50', 'R', '2022-06-08 00:00:00', '2022-06-08 00:00:00', NULL, NULL, NULL, NULL),
(73, 65, 'nl-NL', '', 'http://www.ccoverheid.nl/schema/gwsw/SPARQL', 'T', NULL, 'SPARQL', '', 'SPARQL', 'Een protocol om triplestores met een querytaal te kunnen ondervragen en onderhouden', NULL, NULL, NULL, NULL, '', '', NULL, NULL, '<p>Een belangrijke component is de querytaal waarmee deze bewerkingen worden gedefinieerd.</p>', '', NULL, NULL, NULL, 'Stichting Rioned', 'https://apps.gwsw.nl/doc/GWSW.orox%20Opbouw%20dataset.pdf', NULL, '', 0, NULL, '', NULL, '50', 'R', '2022-06-08 00:00:00', '2022-06-08 00:00:00', NULL, NULL, NULL, NULL),
(74, 1, 'en-GB', '', 'http://www.ccoverheid.nl/schema/rdfs', 'S', 'RDFS', 'RDFS', '', 'RDFS', 'Resource Description Framework Schema', '', '', '', '', 'RDF Schema provides a data-modelling vocabulary for RDF data. RDF Schema is an extension of the basic RDF vocabulary. ', '', '', '', '', '', '', NULL, NULL, '', 'https://www.w3.org/TR/2014/REC-rdf-schema-20140225/', '', '', 0, '', '', 0, '51', 'R', '2022-06-08 00:00:00', '2022-11-05 09:37:12', NULL, NULL, NULL, ''),
